import { Component, OnInit, OnDestroy } from '@angular/core';
import { Step2 } from '../selectplan/selectplan.service';
import { PlanDataService } from '../selectplan/selectplan.dataservice';
import { Step3 } from '../addons/addons.service';
import { AddonsDataService } from '../addons/addons.dataservice';
import { FormBuilder, FormArray, FormGroup, FormControl } from '@angular/forms';
import { Routes, RouterModule, Router } from '@angular/router';

@Component({
  selector: 'app-addons',
  templateUrl: './addons.component.html',
  styleUrls: ['../app.component.css']
})
export class AddonsComponent implements OnInit, OnDestroy {
  step2: Step2;
  Step3: Step3;
  step3: FormGroup;

  onlinechecked: boolean;
  storagechecked: boolean;
  customizablechecked: boolean


  form: FormGroup;
  addons: Array<any> = [
    { name: 'Online', value: 'online', checked: false},
    { name: 'Storage', value: 'storage', checked: false},
    { name: 'Customizable', value: 'customizable', checked: false},
  ];



  constructor(
    public planData: PlanDataService,
    public addonsData: AddonsDataService,
    private router: Router,
    private fb: FormBuilder
  ) {
    this.step3 = this.fb.group({
      addons:  new FormArray([])
     }); 
  }

  onSubmit(){
    this.addonsData.step3 = this.Step3;

    this.router.navigate(['/summary']);
  }

  onCheckboxChange(event: any) {

    const addons = (this.step3.controls['addons'] as FormArray);
    if (event.target.checked) {
      addons.push(new FormControl(event.target.value));
      console.log(event.target.value);
    } else {
      const index = addons.controls
      .findIndex(x => x.value === event.target.value);
      addons.removeAt(index);
    }
    this.Step3 = this.step3.value;
    console.log(addons);
  }



  ngOnInit() {
    this.step2 = this.planData.step2;

    const addons = (this.step3.controls['addons'] as FormArray);

    if(this.addonsData.step3?.addons.includes('online')){
      this.onlinechecked = true;
      addons.push(new FormControl('online'));
    }
    if(this.addonsData.step3?.addons.includes('storage')){
      this.storagechecked = true;
      addons.push(new FormControl('storage'));
    }
    if(this.addonsData.step3?.addons.includes('customizable')){
      this.customizablechecked = true;
      addons.push(new FormControl('customizable'));
    }

    console.log(addons);



  }

  ngOnDestroy(){


    this.Step3 = this.step3.value;
    this.addonsData.step3 = this.Step3;

    console.log(this.step3.value);
    console.log(this.Step3);
    console.log(this.addonsData.step3);
  }

}
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0"> <!-- displays site properly based on user's device -->
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Ubuntu:wght@400;500;700&display=swap" rel="stylesheet">
    <link rel="icon" type="image/png" sizes="32x32" href="./assets/images/favicon-32x32.png">
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossorigin="anonymous">
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css">
    <title>Frontend Mentor | Multi-step form</title>
</head>
<div style="-webkit-user-select: none;-webkit-touch-callout: none;-moz-user-select: none;-webkit-user-drag: none;-ms-user-select: none;user-select: none;">
    <h2 class="title">Pick add-ons</h2>
    <p class="description">Add-ons help enchance your gaming experience.</p>
    <br>
    <form id="step3" [formGroup]="step3" (ngSubmit)="onSubmit()">
        <label class="AddonBox">
            <div class="addoncheck">
                <i class="fa fa-check checkmark"></i>
                <input class="addonOption" type="checkbox" name="addons" value="online" formArrayName="addons" (change)="onCheckboxChange($event)" [checked]="onlinechecked == true"/>
            </div>
            <p class="titlesmall">Online service</p>
            <p class="price2" *ngIf="step2?.monthly == 1 || step2?.monthly == undefined">+$1/mo</p>
            <p class="price2" *ngIf="step2?.monthly == 0">+$10/yr</p>
            <p class="descriptionsmall">Access to multiplayer games</p>

        </label>
        <label class="AddonBox">
            <div class="addoncheck">
                <i class="fa fa-check checkmark"></i>
                <input class="addonOption" type="checkbox" name="addons" value="storage" formArrayName="addons" (change)="onCheckboxChange($event)" [checked]="storagechecked == true"/>
            </div>
            <p class="titlesmall">Larger storage</p>
            <p class="price2" *ngIf="step2?.monthly == 1 || step2?.monthly == undefined">+$2/mo</p>
            <p class="price2" *ngIf="step2?.monthly == 0">+$20/yr</p>
            <p class="descriptionsmall">Extra 1TB of cloud save</p>
        </label>
        <label class="AddonBox">
            <div class="addoncheck">
                <i class="fa fa-check checkmark"></i>
                <input class="addonOption" type="checkbox" name="addons" value="customizable" formArrayName="addons" (change)="onCheckboxChange($event)" [checked]="customizablechecked == true"/>
            </div>
            <p class="titlesmall">Customizable Profile</p>
            <p class="price2" *ngIf="step2?.monthly == 1 || step2?.monthly == undefined">+$2/mo</p>
            <p class="price2" *ngIf="step2?.monthly == 0">+$20/yr</p>
            <p class="descriptionsmall">Custom theme on your profile</p>
        </label>
    </form>
    <div class="NextContainer smallscreen">
    </div>
    <a class="goback" routerLink="/selectplan">Go Back</a>
    <button routerLinkActive="active"class="Next" type="submit" form="step3" value="Submit">Next Step</button>
</div>
